    public GameObject CreateCamera(int resolution)
    {
        GameObject camera = new GameObject();
        camera.name = "Camera";
        Camera cameraCamera =camera.AddComponent<Camera>();
        cameraCamera.orthographic = true;
        cameraCamera.orthographicSize = resolution / 2;
        cameraCamera.allowMSAA = false;
        cameraCamera.backgroundColor = Color.black;
        camera.transform.localPosition = new Vector3(0.0f, 0.0f, -10.0f);
        camera.AddComponent<AudioListener>();
        PixelPerfectCamera cameraPixelPerfectCamera = 
            camera.AddComponent<PixelPerfectCamera>();
        cameraPixelPerfectCamera.assetsPPU = 1;
        cameraPixelPerfectCamera.refResolutionX = resolution;
        cameraPixelPerfectCamera.refResolutionY = resolution;
        cameraPixelPerfectCamera.pixelSnapping = true;
        BoxCollider2D cameraCollider =
            camera.AddComponent<BoxCollider2D>();
        cameraCollider.size = new Vector2(resolution * 2, resolution * 2);
        cameraCollider.isTrigger = true;
        Rigidbody2D cameraRigidBody = camera.AddComponent<Rigidbody2D>();
        cameraRigidBody.isKinematic = true;
        cameraRigidBody.sleepMode = RigidbodySleepMode2D.NeverSleep;

        return camera;
    }

