    IEnumerator BloodCoroutine(SpriteRenderer spriteRenderer, float duration)
    {
        float t = 0.0f;
        float t2 = 0.0f;
        float t2Inc = NumBossBloodSprites / duration;
        int frame = 0;
        while (t < duration)
        {
            while (t2 >= 1.0f)
            {
                frame++;
                t2 -= 1.0f;
            }
            if (frame > NumBossBloodSprites - 1)
                frame = NumBossBloodSprites - 1;
            spriteRenderer.sprite =
                bossBloodSprites[frame];
            t += Time.deltaTime;
            t2 += Time.deltaTime * t2Inc;
            yield return null;
        }
    }

    public GameObject CreateBlood(GameObject parent, Vector2 position)
    {
        float duration = 0.5f;

        GameObject blood = new GameObject();
        blood.name = "Blood";
        blood.transform.position = position;
        blood.transform.parent = parent.transform;
        SpriteRenderer bloodRenderer =
            blood.AddComponent<SpriteRenderer>();
        bloodRenderer.sortingOrder = 7;
        blood.AddComponent<Blood>().StartCoroutine(
            BloodCoroutine(bloodRenderer, duration));
        Destroy(blood, duration);
        return blood;
    }

    // Coroutines

    public IEnumerator ShakeCoroutine(GameObject go, float duration, 
        float magnitude, bool taper = false)
    {
        Vector3 originalPos = go.transform.position;

        float timer = 0f;
        while (timer < duration)
        {
            timer += Time.deltaTime;

            float scale = taper ? 
                (duration - timer) / duration : 
                1.0f;
            float amount = scale * magnitude;
            float xOffset = Random.Range(-amount, amount);
            float yOffset = Random.Range(-amount, amount);

            go.transform.localPosition = originalPos + 
                new Vector3(xOffset, yOffset, 0.0f);

            yield return null;
        }
        go.transform.localPosition = originalPos;
    }

    public IEnumerator MoveToCoroutine(GameObject gameObject, Vector2 pos, float duration,
        bool smooth = false)
    {
        Vector2 initial = gameObject.transform.position;
        float t = 0.0f;
        float inc = 1.0f / duration;
        while (t < 1.0f)
        {
            float t2 = smooth ?
                Mathf.SmoothStep(0.0f, 1.0f, t) :
                t;
            gameObject.transform.position =
                (1.0f - t2) * initial + t2 * pos;
            t += Time.deltaTime * inc;
            yield return null;
        }
    }

    public IEnumerator FollowPathCoroutine(Vector2[] p,
        float startT, float endT, float duration, GameObject gameObject)
    {
        int pathLength = Bezier.LoopLength(p);
        bool reverse = endT < startT;
        float speed = (endT - startT) / duration;
        float t = startT;

        Vector2 startPos = (Vector2)gameObject.transform.localPosition -
            Bezier.CubicLoop(t, p);

        while ((!reverse && t < endT) || (reverse && t > endT))
        {
            gameObject.transform.localPosition = 
                startPos + Bezier.CubicLoop(t, p);
            t += speed * Time.deltaTime;
            yield return null;
        }
    }

